import axios from "axios"

const BASE_URL = "http://localhost:5000/api";
// const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzNmJjYTQxYzI3NWFlMDllMmZlMTliMyIsImlzQWRtaW4iOmZhbHNlLCJpYXQiOjE2NjgwMDg1NDMsImV4cCI6MTY2ODI2Nzc0M30.vs208JpsX4X8ss_tCWtmFwdGeIBd0pRVe5XcAXWe1xA"
// const TOKEN = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser.accessToken;
const TOKEN =  localStorage?.getItem("persist:root")?.currentUser?.accessToken? JSON.parse(localStorage?.getItem("persist:root")) : null



export const pubicRequest = axios.create({
    baseURL:BASE_URL,
});
// export const userRequest = axios.create({
//     baseURL:BASE_URL,
//     header:{token:`Bearer ${TOKEN}`}
// })

// Get user goals
export const userRequest = async (token) => {
    
    const config = {
        headers: {
            Authorization: `Bearer ${TOKEN}`,
        },
    }

    const response = await axios.get(BASE_URL, config)
  
    return response.data
  }
