import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { getPost } from "../redux/features/postSlice";
import "./custom.css";
const Posts = () => {
  const [id, setId] = useState();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, post } = useSelector((state) => ({ ...state.app }));
  const handleFetchData = (e) => {
    e.preventDefault();
    if (!id) {
      window.alert("You dont have an id");
    } else {
      dispatch(getPost({ id }));
      setId("");
    }
  };

  return (
    <div>
      <section className="main_section">
        <form action="#">
          <input
            type="number"
            placeholder="Search ID"
            onChange={(e) => setId(e.target.value)}
          />
          <div className="btns">
            <button type="submit" onClick={handleFetchData}>
              Fetch Post
            </button>
            <button type="button" onClick={() => navigate("/createpost")}>
              Create Post
            </button>
          </div>
        </form>
      </section>
     {post.map((item,id)=>{
      return (
        <section className="post_section" key={id}>
        {loading ? (
          <h1>Loading</h1>
        ) : (
          <>
            {post.length > 0 && (
              <>
                <h3 className="title">{post[0].title}</h3>
                <p className="post">{post[0].body}</p>
                <div className="group_btns">
                  <button className="deleteBtn">Delete</button>
                  <button>Edit</button>
                </div>
              </>
            )}
          </>
        )}
      </section>
      )
     })}
    </div>
  );
};

export default Posts;
