import React from 'react'
import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";

const Slider = () => {
    const handleClick =(direction)=>{}
  return (
   <Container>
     <Arrow direction="left"
     onClick={()=>handleClick("left")}
     >
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper>
        <Slide bg="fcf1ed">
            <ImgContainer>
            <Image src="https://png.pngtree.com/png-vector/20210908/ourmid/pngtree-e-commerce-with-an-illustration-of-a-woman-carrying-groceries-png-image_3917603.jpg"/>
            </ImgContainer>
            <InfoContainer>
              <Title>WINTER SALE</Title>
              <Desc>There is a lot of cloths shoes and many more for boys and girls Come and Grab ir!</Desc>
              <Button>SHOW NOW</Button>
            </InfoContainer>
        </Slide>
        <Slide bg="fcf1ed">
            <ImgContainer>
            <Image src="https://png.pngtree.com/png-vector/20210908/ourmid/pngtree-e-commerce-with-an-illustration-of-a-woman-carrying-groceries-png-image_3917603.jpg"/>
            </ImgContainer>
            <InfoContainer>
              <Title>WINTER SALE</Title>
              <Desc>There is a lot of cloths shoes and many more for boys and girls Come and Grab ir!</Desc>
              <Button>SHOW NOW</Button>
            </InfoContainer>
        </Slide>
        <Slide bg="fcf1ed">
            <ImgContainer>
            <Image src="https://png.pngtree.com/png-vector/20210908/ourmid/pngtree-e-commerce-with-an-illustration-of-a-woman-carrying-groceries-png-image_3917603.jpg"/>
            </ImgContainer>
            <InfoContainer>
              <Title>WINTER SALE</Title>
              <Desc>There is a lot of cloths shoes and many more for boys and girls Come and Grab ir!</Desc>
              <Button>SHOW NOW</Button>
            </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
      onClick={()=>handleClick("right")}
        <ArrowRightOutlined />
      </Arrow>
   </Container>
  )
}


const Container = styled.div`
display:flex;
height:100vh ;
width:100%;
position: relative;
overflow: hidden;
`
const Arrow = styled.div`
 width: 50px;
height: 50px;
background-color: #fff7f7;
border-radius: 50%;
display: flex;
align-items: center;
justify-content: center;
position: absolute;
top: 0;
bottom: 0;
margin:auto ;
left:${(props)=>props.direction === "left" && "10px"} ;
right:${(props)=>props.direction === "right" && "10px"} ;
cursor: pointer;
opacity: 0.5;
z-index: 2;
`
const Wrapper = styled.div`
  height: 100%;
  display: flex;
  /* transform: translateX(-100px); */
`

const Slide = styled.div`
width:100vw ;
height: 100vh;
display: flex;
align-items: center;
background-color: #${(props) => props.bg};
`  
const ImgContainer = styled.div`
height: 100%;
flex: 1;
`;

const Image = styled.img`
height: 80%;
`;
const InfoContainer = styled.div`
flex: 1;
padding: 50px;
`;

const Title = styled.h1`
font-size: 70px;
`;

const Desc = styled.p`
margin: 50px 0px;
font-size: 20px;
font-weight: 500;
letter-spacing: 3px;
`;

const Button = styled.button`
padding: 10px;
font-size: 20px;
background-color: transparent;
cursor: pointer;
`;


export default Slider
