import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { json } from "react-router-dom";


// for get posts
export const getPost = createAsyncThunk('post/getPosts',async({id})=>{
    return fetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
    .then((res)=>res.json()
    );
})
// For delete posts
export const deletePost = createAsyncThunk('post/deletePost',async({id})=>{
    return fetch(`https://jsonplaceholder.typicode.com/posts/${id}`,{
        method:'DELETE',
    })
    .then((res)=>res.json()
    );
})
// For delete Create Post
export const createPost = createAsyncThunk('post/createPost',async({values})=>{
    return fetch(`https://jsonplaceholder.typicode.com/posts`,{
        method:'POST',
        headers:{
            Accept :"application/json",
            "Content-type": "application/json"
        },
        body:JSON.stringify({
            title : values.title,
            body: values.body
        })
    })
    .then((res)=>res.json()
    );
})
// For delete Edit Post
export const editPost = createAsyncThunk('post/editPost',async({id,title,body})=>{
    return fetch(`https://jsonplaceholder.typicode.com/posts/${id}`,{
        method:'PATCH',
        headers:{
            Accept :"application/json",
            "Content-type": "application/json"
        },
        body:JSON.stringify({
            title,
            body
        })
    })
    .then((res)=>res.json()
    );
})

const postSlice = createSlice({
    name: "post",
    initialState: {
      post: [],
      loading: false,
      error: null,
      post:"",  
      edit: false,

    },
    reducers:{
        setEdid:(state,action)=>{
            state.edit = action.payload.edit;
            state.body = action.payload.body;
        }
    },
    extraReducers:{
        // For Get Posts
        [getPost.pending]:(state,action)=>{
            state.loading = true;
        },
        [getPost.fulfilled]:(state,action)=>{
            state.loading = false;
            state.post = [action.payload]
        },
        [getPost.rejected]:(state,action)=>{
            state.loading = false;
            state.error = action.payload
        },
        // For Delete Posts
        [deletePost.pending]:(state,action)=>{
            state.loading = true;
        },
        [deletePost.fulfilled]:(state,action)=>{
            state.loading = false;
            state.post = [action.payload]
        },
        [deletePost.rejected]:(state,action)=>{
            state.loading = false;
            state.error = action.payload
        },
        // For Create Posts
        [createPost.pending]:(state,action)=>{
            state.loading = true;
        },
        [createPost.fulfilled]:(state,action)=>{
            state.loading = false;
            state.post = [action.payload]
        },
        [createPost.rejected]:(state,action)=>{
            state.loading = false;
            state.error = action.payload
        },
    
        // For Create Posts
        [editPost.pending]:(state,action)=>{
            state.loading = true;
        },
        [editPost.fulfilled]:(state,action)=>{
            state.loading = false;
            state.post = [action.payload]
        },
        [editPost.rejected]:(state,action)=>{
            state.loading = false;
            state.error = action.payload
        },
    }
    
})
export default postSlice.reducer;
export const {setEdid} = postSlice.actions
