import { loginStart,loginSuccess,loginFailure,userRegister } from "./userSlice"
// import {publicRequest} from '../requestMethods'
import { pubicRequest } from "../requestMethods";


export const login = async (dispatch , user)=>{
      dispatch(loginStart())
      try {
        const res = await pubicRequest.post("auth/login",user)
        dispatch(loginSuccess(res.data))
      } catch (error) {
        dispatch(loginFailure())
      }

}
export const signup =async (dispatch,user)=>{
  try {
    const res = await pubicRequest.post('auth/register',user);
    dispatch(userRegister(res.data))
  } catch (error) {
    
  }

}
