import { Container } from '@material-ui/core'
import React from 'react'
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { createPost } from '../redux/features/postSlice';
import "./custom.css";

const CreatePost = () => {

   const [values,setValues] = useState({title:"",body:""})
   const [showPost,setShowPost] = useState(false)
   const {title,body} = values;
   const dispatch = useDispatch()
   const navigate = useNavigate()
   const {loading,post} = useSelector((state)=>({...state.app}))
   
   const handleSubmit=(e)=>{
    e.preventDefault();
    dispatch(createPost({values}));
    setValues("")
    setShowPost(true)
   }
  const createdPost =()=>{
    return(
      <>
      {loading ? (
        <h1 style={{textAlign:"center"}}>Loading</h1>
      ) : (
            <>
              <div className="post_section">
              <h3 className="title">{post[0]?.title}</h3>
              <p className="post">{post[0]?.body}</p>
              </div>
            </>
      )}
    </>  
    )
  }
  return (

    <div>
       <section className='createPost_section'>
         <form action="#" className='createPost_form'>
          <input type="text"
          placeholder='Post Title'
          value={title}
          onChange={(e)=>setValues({...values,title:e.target.value})}
           />
           <textarea name="post" id="" cols="51" rows="3"
           placeholder='Post Body'
           value={body}
           onChange={(e)=>setValues({...values,body:e.target.value})}
           />
           <div className="createPostsBtns">
            <button onClick={()=>navigate('/')}>Go to Home </button>
            <button
            onClick={handleSubmit}
            >Post</button>
           </div>
         </form>
         <div className="mt-4">{showPost && <div>{createPost()}</div>}</div>
       </section>
       
    </div>
  )
}

export default CreatePost
