import React from 'react'
import styled from 'styled-components'
import StripeCheckout from 'react-stripe-checkout';
 
const KEY = "pk_test_51I5bysCDMSg6mIGeYmFW8jbOopQ1DO7kIbvEqWAIiCt7L2kDbAjhY0NVI7iwuR02WS6CsiSTaO2diqrJ9ReFQUQA00la9VeJwW"

const Pay = () => {
  
    const onToken =(token)=>{
            console.log(token);
    }

  return (
    <Container>
        <StripeCheckout
        name='E-Shop'
        image='https://www.clipartmax.com/png/middle/270-2707415_confirm-icon-payment-success.png'
        billingAddress
        shippingAddress
        description='Your Total amount is $20'
        amount={2000}
        token={onToken}
        stripeKey={KEY}
        >
        <Button>Pay Now</Button>
        </StripeCheckout>
    </Container>
  )
}
const Container = styled.div`
height: 100vh;
display: flex;
justify-content: center;
align-items: center;
`
const Button = styled.button`
width: 100px;
padding: 15px;
background-color: #000;
color: #fff;
border-radius: 8px;
border: none;

`
export default Pay
