import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { getPost,deletePost } from "../redux/features/postSlice";
import { setEdid,editPost } from "../redux/features/postSlice";
import "./custom.css";
const Posts = () => {
  const [id, setId] = useState();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, post,edit,body } = useSelector((state) => ({ ...state.app }));

  const handleFetchData = (e) => {
    e.preventDefault();
    if (!id) {
      window.alert("You dont have an id");
    } else {
      dispatch(getPost({ id }));
      setId("");
    }
  };
  const handleDelete =({id})=>{
    dispatch(deletePost({id:post?.id}))
    // window.location.reload();  
    // window.alert("Post Deleted")
  }

  return (
    <div>
      <section className="main_section">
        <form action="#">
          <input
            type="number"
            placeholder="Search ID"
            onChange={(e) => setId(e.target.value)}
          />
          <div className="btns">
            <button type="submit" onClick={handleFetchData}>
              Fetch Post
            </button>
            <button type="button" onClick={() => navigate("/createpost")}>
              Create Post
            </button>
          </div>
        </form>
      </section>
      <section>
        {loading ? (
          <h1 style={{textAlign:"center"}}>Loading</h1>
        ) : (
          <>
            {post.length > 0 && (
              <>
                <div className="post_section">

                <h3 className="title">{post[0]?.title}</h3>
                {edit ? (<>Editing</>):(<>
                <p className="post">{post[0]?.body}</p>
                </>)}
                <div className="group_btns">
                  <button className="deleteBtn"
                  onClick={handleDelete}
                  >Delete</button>
                  <button
                  onClick={dispatch(setEdid({edit:true,body:post[0].body}))}
                  
                  >Edit</button>
                </div>
                </div>
              </>
            )}
          </>
        )}
      </section>
       <section>
        {loading ? (
          <h1 style={{textAlign:"center"}}>Loading</h1>
        ) : (
          <>
            {post[0]?.length > 0 && (
              <>
                <div className="post_section">

                <h3 className="title">{post[0]?.title}</h3>
                <p className="post">{post[0]?.body}</p>
                <div className="group_btns">
                  <button className="deleteBtn"
                  onClick={handleDelete}
                  >Delete</button>
                  <button>Edit</button>
                </div>
                </div>
              </>
            )}
          </>
        )}
      </section>
    </div>
  );
};

export default Posts;
