import React, { useState } from 'react'
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import styled from "styled-components";
import { login } from '../redux/apiCalls';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Login = () => {
  const [username,setUsername] = useState("")
  const [password,setPassword] = useState("")
  const dispatch = useDispatch();
  const navigate = useNavigate()
  const {isFetching,isError,isSuccess,isLoading} = useSelector((state)=>state.user)
  
  useEffect(()=>{
    if(isSuccess){
      navigate("/", toast.success('Success Notification !', {
        position: toast.POSITION.TOP_RIGHT
    }))
     
    }
    
  },[isSuccess])
  const handleClick=(e)=>{
    e.preventDefault();
    login(dispatch,{username,password})
  }

  return (
    <Container>
      {isSuccess && <LoginSucess>Logged In Successully!</LoginSucess>}
      <Wrapper>
       {isError && <Error>Error! Something went Wrong!</Error>}
        <Title>SIGN IN</Title>
        <Form>
          <Input placeholder="username" 
          onChange={(e)=>setUsername(e.target.value)}
          />
          <Input placeholder="password"
          type='password'
          onChange={(e)=>setPassword(e.target.value)}
          />
          <Button
          onClick={handleClick}
          disabled={isFetching}
          >LOGIN</Button>
            <ToastContainer />
          <Link>DO NOT YOU REMEMBER THE PASSWORD?</Link>
          <Link>CREATE A NEW ACCOUNT</Link>
        </Form>
      </Wrapper>
    </Container>
  )
}
const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(
      rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),
    url("https://images.pexels.com/photos/6984650/pexels-photo-6984650.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940")
      center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`;
const Error = styled.span`
   width: 400px;
   height: 60px;
   background-color: red;
   color: white;
   font-size: 16px;
   text-align: center;
`
const LoginSucess = styled.span`
   width: 300px;
   height: 60px;
   background-color: green;
   color: white;
   font-size: 20px;
`

const Wrapper = styled.div`
  width: 25%;
  padding: 20px;
  background-color: white;
`;

const Title = styled.h1`
  font-size: 24px;
  font-weight: 300;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  flex: 1;
  min-width: 40%;
  margin: 10px 0;
  padding: 10px;
`;

const Button = styled.button`
  width: 40%;
  border: none;
  padding: 15px 20px;
  background-color: teal;
  color: white;
  cursor: pointer;
  margin-bottom: 10px;
  &::disabled{
    color:green;
    cursor: not-allowed;
  }
`;

const Link = styled.a`
  margin: 5px 0px;
  font-size: 12px;
  text-decoration: underline;
  cursor: pointer;
`;
export default Login
