import React from 'react'
import styled from 'styled-components'

const Success = () => {
    const Container = styled.div`
        height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
    `
       const AmountPaid = styled.div`
         width: fit-content;
        padding: 15px;
        background-color: #393E46;
        color: #fff;
        border-radius: 8px;
        border: none;

 `
 const Back = styled.div`
   width: fit-content;
        padding: 15px;
        background-color: #FDEFF4;
        color: #000;
        border-radius: 8px;
        border: none;
 `
  return (
    <Container>
        <AmountPaid>Amount Paid Succcessfully</AmountPaid>
        <Back>Back to Home</Back>
    </Container>
  )
}

export default Success
